import React, { Component } from "react";
import axios from "axios";

// const cc = require("cryptocompare");

export default class CoinsLoading extends Component {
  constructor(props) {
    super(props);

    this.state = {
      coinList: []
    };
  }

  componentDidMount = () => {
    axios
      .get("https://min-api.cryptocompare.com/data/all/coinlist")
      .then(res => {
        const coinList = res.data.Data;
        console.log(coinList);
        this.setState({ coinList });
      });
  };

  render() {
    const lista = (
      <ul>
        {Object.keys(this.state.coinList)
          .slice(0, 100)
          .map(key => (
            <li key={key.id}>
              <div>
                <h1>{key}</h1>
                <h3>{key.CoinName}</h3>
              </div>
            </li>
          ))}
      </ul>
    );
    const cargandoLista = (
      <ul>
        <li>
          <h2>Cargando todas las Monedas</h2>
        </li>
      </ul>
    );
    return (
      <div className="coinsLoading">
        {this.state.coinList ? lista : cargandoLista}
      </div>
    );
  }

