import React, { Component } from 'react'
import axios from "axios";


export default class extends Component {
  constructor(props) {
    super(props);

    this.state = {
      cryptos: []
    };
  }
  componentDidMount(){
    axios.get('https://min-api.cryptocompare.com/data/price?fsym=BTC&tsyms=USD,EUR,JPY,MXN')
    .then(res => {
      const cryptos = res.data;
      console.log(cryptos);
      this.setState({cryptos: cryptos});
    })
  }
  render(){
    return(
      <div className="barUser">
          {Object.keys(this.state.cryptos).map((key) => (
            <div style={{ color:"white" }} className="dataBarUser">
              {/* <span>{key} </span> */}
              <span>BTC/{key} {this.state.cryptos[key.USD]}   | </span>
              <span>BTC/{key} {this.state.cryptos[EUR]}   | </span>
              <span>BTC/JPY {this.state.cryptos[key]}   | </span>
              <span>BTC/MXN {this.state.cryptos[key]}   | </span>
            </div>

            // <div className="barUser">
            //   <div className="dataBarUser">
            //   <span>BTC/USD {this.state.cryptos[0]} | </span>     
            //   <span>BTC/EUR {this.state.cryptos[1]} | </span>                                       
            //   </div>
            // </div>

          ))}
          </div>
    )
  }

}
