import React, { Component } from 'react'
import axios from 'axios';

const cc = require("cryptocompare");


export default class CoinsLoading extends Component {

  constructor(props) {
    super(props);

    this.state = {
      coinList: []
    };
  }


  // componentDidMount = () => {
  //   this.fetchCoins();
  // };

  // fetchCoins = async () => {
  //   let coinList = (await cc.coinList()).Data;
  //   console.log(coinList);
  //   this.setState({ coinList });
  // };

  componentDidMount = () => {
    axios
      .get("https://min-api.cryptocompare.com/data/v2/news/?lang=ES")
      .then(res => {
        const coinList = res.data.Data;

        this.setState({ coinList });
      });
  };

  render() {
    return (
      <div>


          {this.state.coinList.map(e => (
              <div style={{color:"black"}} key={e.id}>
                {this.state.coinList.BTC}
              </div>
            ))}



      </div>
    )
  }
}
