import axios from 'axios';

export default class NewsEnglish extends Component {
constructor(props){
 super(props);

 this.state={
  english:[]
 }
}

componentWillMount(){

  var getFullURL = function(url, options){
    const params = [];
    for (let key in options) {
        params.push(`${key}=${options[key]}`);
    }
    return url + '?' + params.join("&");
}

var apiKey = "cb0651451a197dfd1bb8c38e722be7c1eaf436b567eb027eedf3444322a2781e";

var baseURL = "https://min-api.cryptocompare.com/data/price"

var options = {
    fsym: "BTC",
    tsyms: "USD"
};

var headers = {
   "Authorization": "Apikey " + apiKey 
};

var fullURL = getFullURL(baseURL, options);

axios.get(fullURL, {headers: headers})
  .then(function(response) {
