import React, { Component } from "react";


export default class Provider extends Component {

  Context = createContext();


  constructor(props) {
    super(props);
    this.state = {
      page: "Inicio"
    };
  }
  setPage = page => this.setState({ page });

  render() {
    return (
      <Context.Provider value={this.state}>
        {this.props.children}
      </Context.Provider>
    );
  }
}
