import React, { Component } from "react";
import axios from "axios";
import { tsThisType } from "@babel/types";

export default class News extends Component {
  constructor(props) {
    super(props);

    this.state = {
      spanish: [],
      spanish2: []
    };
  }

  componentDidMount = () => {
    axios
      .get("https://min-api.cryptocompare.com/data/v2/news/?lang=ES")
      .then(res => {
        const spanish = res.data.Data;
        const spanish2 = res.data.Data[49];
        spanish2.source =
          spanish2.source.charAt(0).toUpperCase() + spanish2.source.substr(1);

        this.setState({ spanish });

      });
  };

  render() {
    const facebookShare = `https://www.facebook.com/sharer/sharer.php?u=${this.state.spanish.guid}`;

    return (
      <div className="containerNews">
        <div className="news">
          <ul>
            <li>
              {this.state.spanish.slice(46, 49).map(e => (
                <div className="news1" key={e.id}>
                  <h1> {e.title}.</h1>
                  <h3>
                    <i>Fuente: </i>
                    {e.source}
                  </h3>
                  <a
                    href={e.guid}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Leer Noticia{" "}
                  </a>
                  <img src={e.imageurl} alt="News" />
                </div>
              ))}

              {/* <div
                class="fb-share-button"
                data-href="https://developers.facebook.com/docs/plugins/"
                data-layout="button"
                data-size="small"
              >
                <a
                  target="_blank"
                  href={facebookShare}
                  class="fb-xfbml-parse-ignore"
                >
                  Compartir
                </a>
              </div> */}
            </li>
          </ul>
        </div>

        <div className="newsSpanish">
          <div className="textNews">
            <h3>
              Descrube todo <br />
              lo que pasa en <br />
              el mundo de las <br /> criptomonedas.
            </h3>
            <a href="/newsEnglish">Noticias en Ingl√©s</a>
          </div>
          {this.state.spanish.map(e => (
            <div className="containerNew" key={e.id}>
              <ul>
                <li>
                  <img src={e.imageurl} alt="" />
                  <h1>{e.title}</h1>
                  <h3>
                    <i>Fuente: </i>
                    {e.source}
                  </h3>
                  <a href={e.guid} target="_blank" rel="noopener noreferrer">
                    Leer Noticia{" "}
                  </a>
                </li>
              </ul>
            </div>
          ))}
        </div>
      </div>
    );
  }
}
