'use strict';
/* global fetch */

const baseUrl = 'https://min-api.cryptocompare.com/data/';
let apiKey = '';

function setApiKey(userApiKey) {
  apiKey = userApiKey;
}

function fetchJSON(url) {
  if (apiKey !== '') {
    if (url.indexOf('?') > -1) {
      url += '&api_key=';
    } else {
      url += '?api_key=';
    }

    url += apiKey;
  }

  return fetch(url).then(res => {
    if (!res.ok) {
      throw new Error("".concat(res.status, " ").concat(res.statusText));
    }

    return res.json();
  }).then(body => {
    if (body.Response === 'Error') throw body.Message;
    return body;
  });
}

