function histoSocial(timePeriod, options) {
  options = options || {};
  let url = "".concat(baseUrl, "social/coin/histo/").concat(timePeriod === 'hour' ? 'hour' : 'day');
  let query = [];
  if (options.coinId) query.push("coinId=".concat(options.coinId));
  if (options.aggregate >= 1 && options.aggregate <= 30) query.push("aggregate=".concat(options.aggregate));
  if (options.aggregate && typeof options.aggregatePredictableTimePeriods === 'boolean') query.push("&aggregatePredictableTimePeriods=".concat(options.aggregatePredictableTimePeriods));
  if (options.limit >= 1 && options.limit <= 2000) query.push("limit=".concat(options.limit));
  if (options.toTs) query.push("toTs=".concat(options.toTs));
  return fetchJSON("".concat(url).concat(query.length > 0 ? '?' + query.join('&') : '')).then(result => result.Data);
}

