import React, { Component } from 'react'
import axios from 'axios';


export default class News extends Component {
  constructor(props){
    super(props)

    this.state = {
      spanish:[]
    }
  }


  componentDidMount = () => {

      axios.get('https://min-api.cryptocompare.com/data/v2/news/?lang=ES')
      .then(res => {
        const spanish = res.data.Data
        this.setState({spanish});
        console.log(spanish);
      })
  }

  render() {
    return (
      <div className="containerNews" style={{height:"90vh"}}>
        <div className="newsSpanish">
            <h1>
              {this.state.spanish.body}
            </h1>
        </div>
      </div>
    )
  }
}
