import React, { Component } from 'react'
import axios from "axios";


export default class extends Component {
  constructor(props) {
    super(props);

    this.state = {
      cryptos: [],
      cryptos2:[],
      cryptos3:[],
      cryptos4:[],
      cryptos5:[]
    };
  }
  componentDidMount(){
    axios.get('https://min-api.cryptocompare.com/data/price?fsym=BTC&tsyms=USD,EUR,MXN')
    .then(res => {
      const cryptos = res.data;
      console.log(cryptos);
      this.setState({cryptos: cryptos});
    })
    axios.get('https://min-api.cryptocompare.com/data/price?fsym=ETH&tsyms=USD,EUR,MXN')
    .then(res => {
      const cryptos2 = res.data;
      console.log(cryptos2);
      this.setState({cryptos2: cryptos2});
    })
    axios.get('https://min-api.cryptocompare.com/data/price?fsym=XRP&tsyms=USD,EUR,MXN')
    .then(res => {
      const cryptos3 = res.data;
      console.log(cryptos3);
      this.setState({cryptos3: cryptos3});
    })
    axios.get('https://min-api.cryptocompare.com/data/price?fsym=LTC&tsyms=USD,EUR,MXN')
    .then(res => {
      const cryptos4 = res.data;
      console.log(cryptos4);
      this.setState({cryptos4: cryptos4});
    })
    axios.get('https://min-api.cryptocompare.com/data/price?fsym=BCH&tsyms=USD,EUR,MXN')
    .then(res => {
      const cryptos5 = res.data;
      console.log(cryptos5);
      this.setState({cryptos5});
    })
  }
  render(){
    return(
      // <div className="barUser">
      //     {Object.keys(this.state.cryptos).map((key) => (
      //       <div style={{ color:"white" }} className="dataBarUser">
      //         <span>
      //           {key}
      //         </span>
      //         <span>
      //           {this.state.cryptos[key].USD}
      //         </span>
      //       </div>

            <div className="barUser">
              <div className="dataBarUser">
                {/* BTC */}
              <span>BTC/USD {this.state.cryptos.USD} | </span>     
              <span>BTC/EUR {this.state.cryptos.EUR} | </span>                                    
              <span>BTC/MXN {this.state.cryptos.MXN} | </span>  
                {/* ETH */}
              <span>ETH/USD {this.state.cryptos2.USD} | </span>  
              <span>ETH/EUR {this.state.cryptos2.EUR} | </span>  
              <span>ETH/MXN {this.state.cryptos2.MXN} | </span>  
                {/* XRP  */}
              <span>XRP/USD {this.state.cryptos3.USD} | </span>  
              <span>XRP/EUR {this.state.cryptos3.EUR} | </span>  
              <span>XRP/MXN {this.state.cryptos3.MXN} | </span>  
                {/* LTC */}
              <span>LTC/USD {this.state.cryptos4.USD} | </span>  
              <span>LTC/EUR {this.state.cryptos4.EUR} | </span>  
              <span>LTC/MXN {this.state.cryptos4.MXN} | </span>  
                {/* BCH */}
              <span>BCH/USD {this.state.cryptos5.USD} | </span>  
              <span>BCH/EUR {this.state.cryptos5.EUR} | </span>  
              <span>BCH/MXN {this.state.cryptos5.MXN} | </span>  

              </div>
            </div>

    //       ))}
    //       </div>
    // )


    )

  }
}
